#!/system/bin/sh
### FDE.AI v3 | FeraVolt. 2019 ###
B=/system/etc/fde.ai/busybox;$B echo "fde_ai">/sys/power/wake_lock;while true;do BOOT=$(getprop sys.boot_completed);if [ "$BOOT" -eq "1" ];then $B sleep 15;break;else $B sleep 3;fi;done;mount -o remount,rw /;mount -o remount rw /;$B mount -o remount,rw /;$B rm -Rf /fde.ai;$B mkdir /fde.ai;mount -t tmpfs -o size=6M tmpfs /fde.ai;$B mount -t tmpfs -o size=6M tmpfs /fde.ai;$B sleep 0.5;$B cp -f /system/etc/fde.ai/busybox /fde.ai/busybox;$B unzip -o /system/etc/fde.ai/f -d /fde.ai/;$B uudecode -o /fde.ai/x /fde.ai/r;$B rm -f /fde.ai/r;$B uudecode -o /fde.ai/s /fde.ai/x;$B rm -f /fde.ai/x;$B uudecode -o /fde.ai/m /fde.ai/s;$B rm -f /fde.ai/s;$B uudecode -o /fde.ai/f /fde.ai/m;$B rm -f /fde.ai/m;$B unzip -o /fde.ai/f -d /fde.ai/;$B sleep 1;$B rm -f /fde.ai/f;$B chmod 777 /fde.ai/*;$B unzip -o /fde.ai/s/rr -d /fde.ai/s/;$B uudecode -o /fde.ai/s/rre.sh /fde.ai/s/rr.bin;$B rm -f /fde.ai/s/rr;$B rm -f /fde.ai/s/rr.bin;$B chmod 777 /fde.ai/s/rre.sh;if [ -e /data/fprop ];then while IFS='' read -r p;do setprop $($B echo "$p");done</data/fprop;fi;$B sleep 1;/fde.ai/busybox cat /fde.ai/s/rre.sh|$B base64 -d|sh;$B rm -f /fde.ai/s/rre.sh;$B echo "fde_ai">/sys/power/wake_unlock;mount -o remount,ro /;mount -o remount ro /;$B mount -o remount,ro /;exit;
