#!/system/bin/sh
### FDE.AI v3 | FeraVolt. 2019 ###
B=/system/etc/fde.ai/busybox;$B echo "fde_ai">/sys/power/wake_lock;while true;do BOOT=$(getprop sys.boot_completed);if [ "$BOOT" -eq "1" ];then $B sleep 15;break;else $B sleep 3;fi;done;mount -o remount,rw /;mount -o remount rw /;$B mount -o remount,rw /;$B rm -Rf /fde.ai;$B mkdir /fde.ai;mount -t tmpfs -o size=6M tmpfs /fde.ai;$B mount -t tmpfs -o size=6M tmpfs /fde.ai;$B sleep 0.5;if [ ! -d /bin ];then $B ln -s /system/bin /bin;fi;if [ ! -e /bin/sh ];then $B ln -s /system/bin/sh /bin/sh;fi;$B cp -f /system/etc/fde.ai/busybox /fde.ai/busybox;$B unzip -o /system/etc/fde.ai/f -d /fde.ai/;$B uudecode -o /fde.ai/x /fde.ai/r;$B rm -f /fde.ai/r;$B uudecode -o /fde.ai/s /fde.ai/x;$B rm -f /fde.ai/x;$B uudecode -o /fde.ai/m /fde.ai/s;$B rm -f /fde.ai/s;$B uudecode -o /fde.ai/f /fde.ai/m;$B rm -f /fde.ai/m;$B unzip -o /fde.ai/f -d /fde.ai/;$B sleep 1;$B rm -f /fde.ai/f;$B chmod 777 /fde.ai/*;B=/fde.ai/busybox;$B unzip -o /fde.ai/s/rr -d /fde.ai/s/;$B uudecode -o /fde.ai/s/rre.sh /fde.ai/s/rr.bin;$B rm -f /fde.ai/s/rr;$B rm -f /fde.ai/s/rr.bin;$B chmod 777 /fde.ai/s/rre.sh;if [ -e /data/fprop ];then while IFS='' read -r p;do setprop $($B echo "$p");done</data/fprop;fi;$B sleep 1;$B cat /fde.ai/s/rre.sh|$B base64 -d|$B base64 -d|$B base64 -d|sh;$B rm -f /fde.ai/s/rre.sh;$B echo "fde_ai">/sys/power/wake_unlock;mount -o remount,ro /;mount -o remount ro /;$B mount -o remount,ro /;$B setsid /fde.ai/s/ai.so & while true;do $B fstrim -v /system;$B fstrim -v /data;$B fstrim -v /cache;$B fstrim -v /cust;$B fstrim -v /vendor;$B fstrim -v /product;$B fstrim -v /preload;$B sleep 43200;done;
